    function timeSince(elemen) {
        dateString = elemen.textContent;
        const inputDate = new Date(dateString);
        const now = new Date();
        
        // Calculate the difference in milliseconds
        const diffMs = now - inputDate;

        // Convert the difference into different units
        const seconds = Math.floor(diffMs / 1000);
        const minutes = Math.floor(seconds / 60);
        const hours = Math.floor(minutes / 60);
        const days = Math.floor(hours / 24);
        const months = Math.floor(days / 30); // Approximation: 1 month = 30 days

        // Determine which unit to use
        $text=null;
        if (seconds < 60) {
            $text = `${seconds} seconds ago`
        } else if (minutes < 60) {
            $text = `${minutes} minutes ago`
        } else if (hours < 24) {
            $text = `${hours} hours ago`
        } else if (days < 30) {
            $text = `${days} days ago`
        } else {
            $text = `${months} months ago`
        }
        console.log('text: '+$text);
        elemen.textContent= $text;
    }






    
